mapper.delete:
DELETE FROM gate_user_intime
WHERE 1=1

    AND id = #{id}


    AND login_name = #{loginName}


    AND name = #{name}


    AND sex = #{sex}


    AND mobile = #{mobile}


    AND tel = #{tel}


    AND address = #{address}


    AND email = #{email}


    AND create_time = #{createTime}


    AND create_user = #{createUser}


    AND update_time = #{updateTime}


    AND update_user = #{updateUser}


    AND status = #{status}


    AND version = #{version}


    AND password = #{password}


    AND default_organid = #{defaultOrganid}


    AND idd = #{idd}


    AND org_id = #{orgId}


    AND org_name = #{orgName}


    AND tenant_code = #{tenantCode}


    AND cn = #{cn}


    AND promotion_url_tent = #{promotionUrlTent}


    AND promotion_url_hp = #{promotionUrlHp}


    AND promotion_url_apple = #{promotionUrlApple}


    AND role = #{role}


    AND performance_examination = #{performanceExamination}


    AND user_img_url = #{userImgUrl}


    AND mobilephone = #{mobilephone}


    AND pwd_sign = #{pwdSign}


    AND app_uid = #{appUid}


    AND ip_limit = #{ipLimit}


    AND need_captcha = #{needCaptcha}


    AND is_white_list_user = #{isWhiteListUser}


    AND is_enabled = #{isEnabled}


    AND update_state_time = #{updateStateTime}


    AND update_state_user = #{updateStateUser}


    AND update_is_enabled_time = #{updateIsEnabledTime}


    AND update_is_enabled_user = #{updateIsEnabledUser}


    AND password_expire_date = #{passwordExpireDate}


    AND last_login_time = #{lastLoginTime}


    AND last_time_password = #{lastTimePassword}


    AND machine_limit = #{machineLimit}


    AND sys = #{sys}


    AND system = #{system}


    AND kz_ticket = #{kzTicket}


    AND kz_user_id = #{kzUserId}

